  private UserViewModel viewModel;
  private String TAG = "MainActivity";


  @Override
  protected void onCreate(Bundle savedInstanceState) {
    super.onCreate(savedInstanceState);
    setContentView(R.layout.activity_main);
    SwipeRefreshLayout swipeContainer = findViewById(R.id.sw);
    TextView textView = findViewById(R.id.text);

    textView.setOnClickListener(new View.OnClickListener() {
      @Override
      public void onClick(View v) {
      }
    });

    RecyclerView recyclerView = findViewById(R.id.userList);
    recyclerView.setLayoutManager(new LinearLayoutManager(getApplication(), RecyclerView.VERTICAL, false));

    final UserAdapter userUserAdapter = new UserAdapter(this);

    viewModel = ViewModelProviders.of(this).get(UserViewModel.class);

    viewModel.userList.observe(this, pagedList -> {
      userUserAdapter.submitList(pagedList);
    });

    viewModel.networkState.observe(this, networkState -> {
      userUserAdapter.setNetworkState(networkState);
      Log.d(TAG, "Network State Change");
    });

    recyclerView.setAdapter(userUserAdapter);
    swipeContainer.setOnRefreshListener(new SwipeRefreshLayout.OnRefreshListener() {
      @Override
      public void onRefresh() {
        userUserAdapter.submitList(null);
        viewModel.getRefreshLiveData().observe(MainActivity.this, new Observer<PagedList<User>>() {
          @Override
          public void onChanged(PagedList<User> users) {
            swipeContainer.setRefreshing(false);
            userUserAdapter.submitList(users);
          }
        });
      }
    });

    Gson gson = new Gson();
    Test test = gson.fromJson(js, Test.class);
    List<Test.DataBean> data = test.getData();
    double volume = 0;
    for (Test.DataBean datum : data) {
      volume = volume + datum.getVolume();
    }
    //一千二百多亿
    textView.setText("100家交易所交易量合计：" + BigDecimal.valueOf(volume));

  }

  @Override
  public void onRetryClick(View view, int position) {
    Log.d(TAG, "Position " + position);
  }
